const Book = (props) => {
  return (
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: 128,
            height: 188,
            backgroundImage: `url("${props.book.imageLinks.thumbnail}")`,
          }}
        />
        <div className="book-shelf-changer">
          <select value={props.shelf}>

          </select>

        </div>
      </div>
    </div>
  );
};
